-- -----------------------------------------------------
-- View `trabalho_banco`.`sales_by_store`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `trabalho_banco`.`sales_by_store`;
USE `trabalho_banco`;
--
-- View structure for view `sales_by_store`
--

CREATE  OR REPLACE VIEW sales_by_store
AS 
SELECT 
CONCAT(c.city, _utf8',', cy.country) AS store
, CONCAT(m.first_name, _utf8' ', m.last_name) AS manager
, SUM(p.amount) AS total_sales
FROM payment AS p
INNER JOIN rental AS r ON p.rental_id = r.rental_id
INNER JOIN inventory AS i ON r.inventory_id = i.inventory_id
INNER JOIN store AS s ON i.store_id = s.store_id
INNER JOIN address AS a ON s.address_id = a.address_id
INNER JOIN city AS c ON a.city_id = c.city_id
INNER JOIN country AS cy ON c.country_id = cy.country_id
INNER JOIN staff AS m ON s.manager_staff_id = m.staff_id
GROUP BY s.store_id
ORDER BY cy.country, c.city;

-- -----------------------------------------------------
-- View `trabalho_banco`.`sales_by_film_category`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `trabalho_banco`.`sales_by_film_category`;
USE `trabalho_banco`;
--
-- View structure for view `sales_by_film_category`
--
-- Note that total sales will add up to >100% because
-- some titles belong to more than 1 category
--

CREATE  OR REPLACE VIEW sales_by_film_category
AS 
SELECT 
c.name AS category
, SUM(p.amount) AS total_sales
FROM payment AS p
INNER JOIN rental AS r ON p.rental_id = r.rental_id
INNER JOIN inventory AS i ON r.inventory_id = i.inventory_id
INNER JOIN film AS f ON i.film_id = f.film_id
INNER JOIN film_category AS fc ON f.film_id = fc.film_id
INNER JOIN category AS c ON fc.category_id = c.category_id
GROUP BY c.name
ORDER BY total_sales DESC;

-- -----------------------------------------------------
-- View `trabalho_banco`.`actor_info`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `trabalho_banco`.`actor_info`;
USE `trabalho_banco`;
--
-- View structure for view `actor_info`
--

CREATE  OR REPLACE DEFINER=CURRENT_USER SQL SECURITY INVOKER VIEW actor_info 
AS
SELECT      
a.actor_id,
a.first_name,
a.last_name,
GROUP_CONCAT(DISTINCT CONCAT(c.name, ': ',
		(SELECT GROUP_CONCAT(f.title ORDER BY f.title SEPARATOR ', ')
                    FROM trabalho_banco.film f
                    INNER JOIN trabalho_banco.film_category fc
                      ON f.film_id = fc.film_id
                    INNER JOIN trabalho_banco.film_actor fa
                      ON f.film_id = fa.film_id
                    WHERE fc.category_id = c.category_id
                    AND fa.actor_id = a.actor_id
                 )
             )
             ORDER BY c.name SEPARATOR '; ')
AS film_info
FROM trabalho_banco.actor a
LEFT JOIN trabalho_banco.film_actor fa
  ON a.actor_id = fa.actor_id
LEFT JOIN trabalho_banco.film_category fc
  ON fa.film_id = fc.film_id
LEFT JOIN trabalho_banco.category c
